import './App.css';
import Footer from './components/Footer/Footer';
import Header from './components/Header/Header';
import Main from './components/Main/Main';
import { useState } from 'react';



function App() {
  const [products, setProducts] = useState([
    { img: require("../src/images/milkshakes/milkshake3.jpg"),
      isChecked: false,
      title: "Milkshake 1111",
      description: "Shake shake shake"
    },
    { img: require("../src/images/milkshakes/milkshake2.jpeg"), isChecked: false,
      title: "Milkshake 2",
      description: "Shake shake shake" 
    },
    { img: require("../src/images/milkshakes/milkshake1.webp"),
      isChecked: false,
      title: "Milkshake 3",
      description: "Shake shake shake"
    },
    { img: require("../src/images/milkshakes/milkshake4.jpeg"),
      isChecked: false,
      title: "Milkshake 4",
      description: "Shake shake shake" 
    }
  ]);
  const handleClick = (index) => {
    const updatedProducts = [...products];
    updatedProducts[index].isChecked = !updatedProducts[index].isChecked;
    setProducts(updatedProducts);
  }
  return (
    <div className='bg'>
      <Header></Header>
      {/* <Main></Main> */}

      <Main products={products} handleClick={handleClick}/>
      <Footer />
      {/* <Footer></Footer> */}
    </div>
  );
}

export default App;
