import React from "react";
import { useState } from "react";
import Products from "../Products/Products";
import "./Main.css";



function Main(){
  const [products, setProducts] = useState([
    { img: require("../../images/milkshakes/milkshake3.jpg"), isChecked: false, title: "Milkshake 1", description: "Shake shake shake" },
    { img: require("../../images/milkshakes/milkshake2.jpeg"), isChecked: false, title: "Milkshake 2", description: "Shake shake shake" },
    { img: require("../../images/milkshakes/milkshake1.webp"), isChecked: false, title: "Milkshake 3", description: "Shake shake shake" },
    { img: require("../../images/milkshakes/milkshake4.jpeg"), isChecked: false, title: "Milkshake 4", description: "Shake shake shake" }
  ]);

  const handleClick = (index) => {
    const updatedProducts = [...products];
    updatedProducts[index].isChecked = !updatedProducts[index].isChecked;
    setProducts(updatedProducts);
  }

  return(
    <main className="wrapper">
      {products.map((product, index) => (
        <Products key={index} img={product.img} title={product.title} description={product.description}  isChecked={product.isChecked}
          onCardClick={() => handleClick(index)}/>
      ))}
    </main>
  )
}

export default Main;
